const slider=document.querySelector(".items");let startX,scrollLeft,isDown=!1;slider.addEventListener("mousedown",(e=>{isDown=!0,slider.classList.add("active"),startX=e.pageX-slider.offsetLeft,scrollLeft=slider.scrollLeft})),slider.addEventListener("mouseleave",(()=>{isDown=!1,slider.classList.remove("active")})),slider.addEventListener("mouseup",(()=>{isDown=!1,slider.classList.remove("active")})),slider.addEventListener("mousemove",(e=>{if(!isDown)return;e.preventDefault();const s=.8*(e.pageX-slider.offsetLeft-startX);slider.scrollLeft=scrollLeft-s;const t=document.querySelectorAll(".img"),r=t.length;for(i=0;i<r;i++){let e=50+432*i/window.innerWidth-s/50;t[i].style.transform="translate3d(-"+e+"%,0,-500px)"}}));